msg.payload = [
    // Step 1: Match records for the given productId and timestamp >= given datetime
    {
        $match: {
            productId: productId,  // Use productId directly
            timestamp: { $gte: new Date(datetime) }  // Ensure datetime is properly converted
        }
     },
    // // Step 2: Sort in ascending order (chronological order)
     {
        $sort: { timestamp: 1 }
     },
    // // Step 3: Use $setWindowFields to create cumulative sum, stopping when currentFlow = 0
   {
        $setWindowFields: {
            partitionBy: "$productId",
            sortBy: { timestamp: 1 },
            output: {
                cumulativeTotalFlow: {
                    $sum: "$currentFlow",
                    window: { documents: ["unbounded", "current"] } // Cumulative sum
                },
                 isZeroFlow: {
                    $min: {
                        $cond: { if: { $eq: ["$currentFlow", 0] }, then: 1, else: 0 }
                    },
                    window: { documents: ["unbounded", "current"] }
                }
            }
        }
    },
    // // Step 4: Filter to keep only records before first occurrence of currentFlow = 0
