                        periods: [],
                        currentPeriod: [],
                        lastFlow: 0
                    },
                    in: {
                        periods: {
                            $concatArrays: [
                                "$$value.periods",
                                {
                                    $cond: {
                                        if: {
                                            $and: [
                                                { $eq: ["$$this.currentFlow", 0] },
                                                { $gt: [{ $size: "$$value.currentPeriod" }, 0] }
                                            ]
                                        },
                                        then: ["$$value.currentPeriod"],
                                        else: []
                                    }
                                }
                            ]
                        },
                        currentPeriod: {
                            $cond: {
                                if: { $eq: ["$$this.currentFlow", 0] },
                                then: [],
